import { useEffect, useState } from 'react';

function Clock() {
    const [date, setDate] = useState(new Date());
    useEffect(() => {
        const intervalID = setInterval(() => {
            setDate(new Date())
        }, 100);
        return () => {
            clearInterval(intervalID)
        };
    }, []);

    return (
        <p>{date.getSeconds()}</p>
    );
}


export default Clock;
